mapper.select:
SELECT *
FROM TB_LECTURE_PLAN
WHERE CLASS_PROF=#{classProf}
AND CLASS_NO=#{classNo}

SELECT *
FROM TB_CLASS_DETAIL
WHERE LECTURE_NO = #{lecNo}

SELECT *
FROM TB_PROF_TEST
WHERE TEST_NO =#{tNo}

mapper.delete:
DELETE FROM TB_TEST
WHERE
TEST_NO =#{tNo}
AND QUESTION_NO =#{qNo}
AND QID =#{qId}

mapper.select:
SELECT *
FROM TB_TEST
WHERE
CLASS_NO= #{cNo}
and NOT test_no= #{tNo}
ORDER BY QID DESC

SELECT *
FROM TB_PROF_TEST
WHERE PROF_NO = #{pNo}

SELECT *
FROM TB_HOMEWORK
WHERE PROF_NO = #{pNo}
AND CLASS_NO =#{cNo}

SELECT *
FROM TB_ASSIGNMENT
where CLASS_NO =#{cNo}
and LECTURE_NO =#{lecNo}
