mapper.select:
	
         SELECT * FROM TESTT T
         LEFT JOIN TB_CLASS C ON(C.CLASS_NO=T.CLASS_NO)
         WHERE STD_NO = #{sNo}

insert.foreach:
INTO TB_SEMESTER_POINT ( STD_NO , SEM_YEAR , SEME_NO , CLASS_NO , MID_TEST , FINAL_TEST , REPORT , ATTENDANCE , POINT , PROF_NO , AVG_POINT
)VALUES (
(SELECT NVL(MAX(STD_SEMESTER),0)+1 FROM TB_GRADE) , #{item.sNo} , DEFAULT , #{item.cNo} , #{item.pNo} )
